import React, { useState } from 'react'
import './expensesForm.css'

const ExpensesForm = () => {
    const [expenseItem,setExpenseItem] = useState({
        "title" : "abc",
        "amount" : "123",
        "date": "2022"
    })
    

    const titleChangeHandler = (e) => {
        setExpenseItem(...expenseItem, title = e.target.value)
    } 

    const amountChangeHandler = (e) => {
        setExpenseItem(...expenseItem, amount = e.target.value)
    }

    const dateChangeHandler = (e) => {
        setExpenseItem(...expenseItem, date = e.target.value)
    }

  return (
    <div className='new-expense__controls'>
      <div className='new-expense__control'>
        <label>Title</label>
        <input type="text" onChange={titleChangeHandler}/>
      </div>
      <div className='new-expense__control'>
        <label>Amount</label>
        <input type="number" min="0" step="10" onChange={amountChangeHandler}/>
      </div>
      <div className='new-expense__control'>
        <label>Date</label>
        <input type="date" min="2019-12-01" max="2023-01-01" onChange={dateChangeHandler}/>
      </div>
      <div className='new-expense__actions'>
        <button type='submit'>Add Expense</button>
      </div>
    </div>
  )
}

export default ExpensesForm
