import React, { useState } from 'react'
import './expensesForm.css'

const ExpensesForm = () => {
    const [expenseItem,setExpenseItem] = useState({
        title : "abc",
        amount : "123",
        date: "2022"
    })
    

    const titleChangeHandler = (e) => {
        setExpenseItem((prevState) => {
            return {...prevState, title : e.target.value}
        })
    } 

    const amountChangeHandler = (e) => {
        setExpenseItem(() => {
            return {
                ...expenseItem, amount : e.target.value
            }
        })
    }

    const dateChangeHandler = (e) => {
        setExpenseItem(() => {
            return {...expenseItem, date : e.target.value}
        })
    }

    const submitHandler = (event) => {
        event.preventDefault()

        const expendsData = expenseItem
        console.log(expendsData)
    }

  return (
    <div className='new-expense__controls'>
        <form onSubmit={submitHandler}>
      <div className='new-expense__control'>
        <label>Title</label>
        <input type="text" onChange={titleChangeHandler} />
      </div>
      <div className='new-expense__control'>
        <label>Amount</label>
        <input type="number" min="0" step="10" onChange={amountChangeHandler}/>
      </div>
      <div className='new-expense__control'>
        <label>Date</label>
        <input type="date" min="2019-12-01" max="2023-01-01" onChange={dateChangeHandler}/>
      </div>
      <div className='new-expense__actions'>
        <button type='submit'>Add Expense</button>
      </div>
      </form>
    </div>
  )
}

export default ExpensesForm
